import React from 'react';
import {connect} from 'react-redux';
import {signIn, signOut} from '../actions';

class GoogleAuth extends React.Component {
    state = {isSignedIn: null};
    // Only want to initialize library once when the class is rendered to the screen
    componentDidMount() {
        window.gapi.load('client:auth2', () => {
            window.gapi.client.init({
                clientId: '305103598371-84lhlc1c1pik0bftgqi86ou70ott5fd9.apps.googleusercontent.com',
                scope: 'email'
            }).then(() => { //client.init returns a promise, invoke arrow function after library has been initiated
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({isSignedIn: this.auth.isSignedIn.get()});
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = () => {
        // Called whenever user authentication status changes
        this.setState({isSignedIn: this.auth.isSignedIn.get()})
    };

    onSignInClick = () => {
        this.auth.signIn();
    };

    onSignOutClick = () => {
        this.auth.signOut();
    };

    renderAuthButton() {
        if (this.state.isSignedIn === null) {
            return null;
        } else if (this.state.isSignedIn) {
            return (
                <button onClick={this.onSignOutClick} className='ui red google button'>
                    <i className='google icon'/>
                    Sign Out
                </button>
            );
        } else {
            return (
                <button onClick={this.onSignInClick} className='ui red google button'>
                    <i className='google icon'/>
                    Sign In With Google
                </button>
            );
        }
    };

    render() {
        return <div>{this.renderAuthButton()}</div>
    };
};

export default connect(null, {signIn, signOut})(GoogleAuth);
